  private apiUrl = 
  username!: string;
  constructor(private http: HttpClient) {

  }

  setUsername(username: string) {
    console.log(username);
    const headers = new HttpHeaders({'myHeader': 'bore'})
    this.http.post<{username: string}>(
      'https://bore-f3b16-default-rtdb.firebaseio.com/',
      username, {headers: headers})
