@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent {
  title = 'BORE';
  subTitle = 'Join a room and share files';

  showRectangle = false;

  
}
