import { User } from './user';

@Component({
  selector: 'app-username',
  templateUrl: './username.component.html',
  styleUrls: ['./username.component.css']
})
export class UsernameComponent {
  usernameForm = '';

  submitted = false;

  onSubmit() {
    this.submitted = true;
  }
}
