import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, throwError } from 'rxjs';
import { catchError, retry } from 'rxjs/operators';

interface UserObject {
  username: string;
}

@Injectable({
  providedIn: 'root'
})
export class UserService {
  name!: string;

  tempName!: string;
  ip = location.host;
  private apiUrl = 'http://' + this.ip + '3621';
  //private user: UserObject = {name: this.name};

  constructor(private http: HttpClient) {}

  setUsername(name: string) {
    this.name = name;
  }

  getUsername(): string {
    return this.name;
  }
  
  
  postUsername(name: string) {
    this.tempName = this.apiUrl + '/setName';
    this.http.post(this.tempName, name, {responseType: 'json'})
    .subscribe((res) => {
      console.log(res);
    });
    
  }

  


  retrieveUsername() {
    //return this.http.get<Confix>
  }
}
